template<typename F>
int get_exec_time(F f) {
	auto s = GetTickCount();
	f();
	return GetTickCount() - s;
}

void test_from_file() {
	std::fstream file("tests.txt", std::ios::in), res_file("res.txt", std::ios::out);
	std::string num1, num2, res;
	while (!file.eof()) {
		std::getline(file, num1);
		std::getline(file, num2);
		std::getline(file, res);
		std::cout << num1.size() << " " << num2.size() << std::endl;
		number n1(num1);
		number n2(num2);
		number number_res;

		std::cout << get_exec_time([&]() {
			number_res = n1 * n2;
		}) << std::endl;
		std::cout << (number_res.tostring() == res) << std::endl;

		std::cout << get_exec_time([&]() {
			number_res = mult(n1, n2);
			}) << std::endl;
		std::cout << (number_res.tostring() == res) << std::endl;

		std::cout << get_exec_time([&]() {
			number_res = fast_mult_async(n1, n2);
			}) << std::endl;
		std::cout << (number_res.tostring() == res) << std::endl;
	}
}

int main() {
	test_from_file();

	return 0;
}
